import React from 'react'
import './home.css';
import * as FaIcons from 'react-icons/fa';
import { gsap } from "gsap";
import { ScrollTrigger } from "gsap/ScrollTrigger";
import { useRef, useEffect } from "react";


const Home = () => {

  gsap.registerPlugin(ScrollTrigger);
  const ref = useRef(null);

  useEffect(() => {
    const element = ref.current;
    gsap.from(
        element.querySelector(".home-text"),
        {
        scrollTrigger: {
            trigger: '.home-text',
            toggleActions: "restart reverse restart reverse",
            //             onEnter onLeave  onEnterBack  onLeaveBack
        },
        y: 80,
        duration: 3,
        opacity: 0,
        ease: 'elastic'
    });
  }, []);

  useEffect(() => {
    const element = ref.current;
    gsap.from(
        element.querySelector(".home-arrow"),
        {
        scrollTrigger: {
            trigger: '.home-arrow',
            toggleActions: "restart reverse restart reverse",
            //             onEnter onLeave  onEnterBack  onLeaveBack
        },
        y: 150,
        duration: 5,
        opacity: 0,
        ease: 'elastic'
    });
  }, []);

    return (
        <>
          <div id='home' ref={ref}>
            <div className='home-text'>
            <p className='title'>Hello, my name is Charlie,</p>
            <p>I am a junior JavaScript developer, currently looking for opportunities in the software development industry. I am a contributor to open-source software and have experience working with React, Node and VanillaJS. Below you can view some of my projects including clones of popular UIâ€™s.</p>
            </div>
            <Arrow link='#project1' />
           </div> 
        </>
    )
}

export default Home
