import React from 'react'
import './home.css';
import * as FaIcons from 'react-icons/fa';
import { gsap } from "gsap";
import { ScrollTrigger } from "gsap/ScrollTrigger";

const Home = () => {

  gsap.registerPlugin(ScrollTrigger);
  const ref = useRef(null);

  useEffect(() => {
    const element = ref.current;
    gsap.from(
        element.querySelector("#project1"),
        {
        scrollTrigger: {
            trigger: '#project1',
            toggleActions: "restart reverse restart reverse",
            //             onEnter onLeave  onEnterBack  onLeaveBack
        },
        x: -600,
        duration: 3,
        opacity: 0,
        ease: 'elastic'
    });
  }, []);

    return (
        <>
          <div id='home' ref={ref}>
            <p className='home-text'><span className='title'>Hello, my name is Charlie,</span><br/> I am a front-end developer from the UK. I program in JavaScript as well as working with many front-end technologies. I specialise in building component-based interfaces with ReactJS.</p>
            {/*<a className='btn btn-outline-success'>view my work<br /><FaIcons.FaArrowCircleDown className='icon'/></a>*/}
           </div> 
        </>
    )
}

export default Home
