import React from 'react';
import { FormControl } from 'react-bootstrap';
import './contact.css';
import { gsap } from "gsap";
import { ScrollTrigger } from "gsap/ScrollTrigger";
import { useRef, useEffect } from "react";


const Contact = () => {

    gsap.registerPlugin(ScrollTrigger);
    const ref = useRef(null);

    
  useEffect(() => {
    const element = ref.current;
    gsap.from(
        element.querySelector(".home-tex"),
        {
        scrollTrigger: {
            trigger: '.home-text',
            toggleActions: "restart reverse restart reverse",
            //             onEnter onLeave  onEnterBack  onLeaveBack
        },
        y: 80,
        duration: 3,
        opacity: 0,
        ease: 'elastic'
    });
  }, []);

    return (
        <>

        <form id='contact' ref={ref} >
            <h1>Get in Touch</h1>
            <FormControl className='input-box' placeholder='Name'/>
            <FormControl className='input-box' placeholder='Email'/>
            <FormControl className='input-box' placeholder='Subject'/>
            <FormControl className='input-box' placeholder='Message'as='textarea' rows='5'/>
            <div className='send-btn'><button type='submit' className='btn btn-outline-success'>Send Message</button></div>

        </form>
         
        </>
    )
}

export default Contact
