import React, { Component } from 'react';
import { FormControl } from 'react-bootstrap';
import './contact.css';
import { useRef, useEffect } from "react";


export default class Form extends Component {

    state={
        name:'',
        email:'',
        subject:'',
        message:'',
        sent:false
    }

    // handle inputs

    handleName=(e)=>{
        this.setState({
            name:e.target.value
    })
    }

    handleEmail=(e)=>{
        this.setState({
            email:e.target.value
    })
    }

    handleSubject=(e)=>{
        this.setState({
            subject:e.target.value
    })
    }

    handleMessage=(e)=>{
        this.setState({
            message:e.target.value
    })
    }



    //end of handle inputs


render() {
    return ( 
        <>

        <form id='contact'>
            <h1>Get in Touch</h1>
            <FormControl className='input-box input1' type='text' placeholder='Name' name='name' value={this.state.name} onChange={this.handleName} />
            <FormControl className='input-box input2' type='text' placeholder='Email' name='email'/>
            <FormControl className='input-box input3' type='text' placeholder='Subject' name='subject'/>
            <FormControl className='input-box input4' type='text' placeholder='Message'as='textarea' rows='5' name='message'/>
            <div className='msg'>Message has been sent</div>
            <div className='send-btn'><button type='submit' className='btn btn-outline-success'>Send Message</button></div>

        </form>
         
        </>
    )
}

}
