import React from 'react';
import { FormControl } from 'react-bootstrap';


const Contact = () => {
    return (
        <>

<form id='contact' onSubmit={handleSubmit}>
            <h1>Get in Touch</h1>
            
            <div className='input-box'><FormControl placeholder='Name' value={name} onChange={(e) => setName(e.target.value)} required="required" id='message'/></div>
            <div className='input-box'><FormControl placeholder='Email' value={email} onChange={(e) => setEmail(e.target.value)} required="required" /></div>
            <div className='input-box'><FormControl placeholder='Subject' value={subject} onChange={(e) => setSubject(e.target.value)} required="required" /></div>
            <div className='input-box'><FormControl placeholder='Message' as='textarea' rows='5' value={message} onChange={(e) => setMessage(e.target.value)} required="required" /></div>
            <div className='sendBtn'><button type='submit' className='btn btn-outline-success'>Send Message</button></div>
        </form>
         
        </>
    )
}

export default Contact
