import React, { Component, useRef, useEffect } from 'react';
import { FormControl } from 'react-bootstrap';
import './contact.css';
import firebase from 'firebase';
import { db } from './firebase';
import emailjs from 'emailjs-com';



export default class Form extends Component {

    
    state={
        name:'',
        email:'',
        subject:'',
        message:'',

        nameError:false,
        emailError:false,
        subjectError:false,
        messageError:false,
        sent:false
    }

    // handle inputs

    handleName=(e)=>{
        this.setState({
            name:e.target.value
    })
    }

    handleEmail=(e)=>{
        this.setState({
            email:e.target.value
    })
    }

    handleSubject=(e)=>{
        this.setState({
            subject:e.target.value
    })
    }

    handleMessage=(e)=>{
        this.setState({
            message:e.target.value
    })
    }

    
    formSubmit=(e)=>{

        //form validation
        const validateForm = () => {
        
            let myregex = new RegExp(/^[a-zA-Z0-9 _+-]*$/);
            let emailRegex = new RegExp(/^([a-zA-Z0-9_\-\.]+)@([a-zA-Z0-9_\-\.]+)\.([a-zA-Z]{2,5})$/);
            let name = document.getElementById('name').value;
            let email = document.getElementById('email').value;
            let subject = document.getElementById('subject').value;
            let message = document.getElementById('message').value;

            if(!myregex.test(name))
            {
                this.setState({nameError:true, emailError:false, subjectError:false, messageError:false, sent:false});
                //return false;
            }
            
            if(!emailRegex.test(email))
            {
                this.setState({nameError:false, emailError:true, subjectError:false, messageError:false, sent:false});
                return false;
            }
            
            if(!myregex.test(subject))
            {
                this.setState({nameError:false, emailError:false, subjectError:true, messageError:false, sent:false});
                return false;
            }

            if(!myregex.test(message))
            {
                this.setState({nameError:false, emailError:false, subjectError:false, messageError:true, sent:false});
                return false;
            }

            return true;
        }
        
        if(validateForm() === true) {

          e.preventDefault();

          emailjs.sendForm('service_92p2bns', 'template_t2t9sb3', e.target, 'user_hcMT3qjFKeDeR3KT4Zt04')
            .then((result) => {
                console.log(result.text);
            }, (error) => {
                console.log(error.text);
            });

            this.setState({
              name:'',
              email:'',
              subject:'',
              message:''
          })
            
    }

    }

render() {
    return ( 
        <>

        <form onSubmit={this.formSubmit} id='contact'>
            <h1>Get in Touch</h1>
            <FormControl className='input-box input1' type='text' placeholder='Name' id='name' name='name' value={this.state.name} onChange={this.handleName} required/>
            <FormControl className='input-box input2' type='text' placeholder='Email' id='email' name='email' value={this.state.email} onChange={this.handleEmail} required/>
            <FormControl className='input-box input3' type='text' placeholder='Subject' id='subject' name='subject' value={this.state.subject} onChange={this.handleSubject} required/>
            <FormControl className='input-box input4' type='text' placeholder='Message'as='textarea' rows='5' id='message' name='message' value={this.state.message} onChange={this.handleMessage} required/>
            
            <div className='msg'>
            {this.state.sent?<div>Message has been sent</div>:null}
            {this.state.nameError?<div>Name not valid</div>:null}
            {this.state.emailError?<div>Email not valid</div>:null}
            {this.state.subjectError?<div>Subject not valid</div>:null}
            {this.state.messageError?<div>Message not valid</div>:null}  
            </div>
  
            <div className='send-btn'><button type='submit' className='btn btn-outline-success send'>Send Message</button></div>

        </form>
         
        </>
    )
}

}
