import React, { Component, useRef, useEffect } from 'react';
import { FormControl } from 'react-bootstrap';
import './contact.css';
import axios from 'axios';
import { gsap } from "gsap";
import { ScrollTrigger } from "gsap/ScrollTrigger";


export default class Form extends Component {

    
  
      

render() {
    return ( 
        <>

        <form onSubmit={this.formSubmit} id='contact'>
            <h1>Get in Touch</h1>
            <FormControl className='input-box input1' type='text' placeholder='Name' id='name'  required/>
            <FormControl className='input-box input2' type='text' placeholder='Email' id='email' value={this.state.email} onChange={this.handleEmail} required/>
            <FormControl className='input-box input3' type='text' placeholder='Subject' id='subject' value={this.state.subject} onChange={this.handleSubject} required/>
            <FormControl className='input-box input4' type='text' placeholder='Message'as='textarea' rows='5' id='message'value={this.state.message} onChange={this.handleMessage} required/>
            
            <div className='send-btn'><button type='submit' className='btn btn-outline-success send'>Send Message</button></div>

        </form>
         
        </>
    )
}

}
