import { useRef, useEffect } from "react";
import Iframe from 'react-iframe';
import './project.css';
import { gsap } from "gsap";
import { ScrollTrigger } from "gsap/ScrollTrigger";
import Arrow from '../Arrow/Arrow';

const Projects = () => {

    gsap.registerPlugin(ScrollTrigger);
    const ref = useRef(null);
  

    useEffect(() => {
        const element = ref.current;
        gsap.from(
            element.querySelector("#project1"),
            {
            scrollTrigger: {
                trigger: '#project1',
                toggleActions: "restart reverse restart reverse",
                //             onEnter onLeave  onEnterBack  onLeaveBack
            },
            x: -600,
            duration: 3,
            opacity: 0,
            ease: 'elastic'
        });
      }, []);

      useEffect(() => {
        const element = ref.current;
        gsap.from(
            element.querySelector("#btn1"),
            {
            scrollTrigger: {
                trigger: '#btn1',
                toggleActions: "restart reverse restart reverse",
                //             onEnter onLeave  onEnterBack  onLeaveBack
            },
            x: -600,
            duration: 6,
            opacity: 0,
            ease: 'elastic'
        });
      }, []);

      useEffect(() => {
        const element = ref.current;
        gsap.from(
            element.querySelector("#project2"),
            {
            scrollTrigger: {
                trigger: '#project2',
                toggleActions: "restart reverse restart reverse",
                //             onEnter onLeave  onEnterBack  onLeaveBack
            },
            x: 600,
            duration: 3,
            opacity: 0,
            ease: 'elastic'
        });
      }, []);

      useEffect(() => {
        const element = ref.current;
        gsap.from(
            element.querySelector("#btn2"),
            {
            scrollTrigger: {
                trigger: '#btn2',
                toggleActions: "restart reverse restart reverse",
                //             onEnter onLeave  onEnterBack  onLeaveBack
            },
            x: 600,
            duration: 6,
            opacity: 0,
            ease: 'elastic'
        });
      }, []);

      useEffect(() => {
        const element = ref.current;
        gsap.from(
            element.querySelector("#project3"),
            {
            scrollTrigger: {
                trigger: '#project3',
                toggleActions: "restart reverse restart reverse",
                //             onEnter onLeave  onEnterBack  onLeaveBack
            },
            x: -600,
            duration: 3,
            opacity: 0,
            ease: 'elastic'
        });
      }, []);

      
      useEffect(() => {
        const element = ref.current;
        gsap.from(
            element.querySelector("#btn3"),
            {
            scrollTrigger: {
                trigger: '#btn3',
                toggleActions: "restart reverse restart reverse",
                //             onEnter onLeave  onEnterBack  onLeaveBack
            },
            x: -600,
            duration: 6,
            opacity: 0,
            ease: 'elastic'
        });
      }, []);

      useEffect(() => {
        const element = ref.current;
        gsap.from(
            element.querySelector("#btn3s"),
            {
            scrollTrigger: {
                trigger: '#btn3s',
                toggleActions: "restart reverse restart reverse",
                //             onEnter onLeave  onEnterBack  onLeaveBack
            },
            x: -600,
            duration: 6,
            opacity: 0,
            ease: 'elastic'
        });
      }, []);

      
      
    return (
        <div ref={ref}>
        
        <div>
        <Iframe url={'https://charlielaver.github.io/netflix-in-react-deploy/'} className="project" id='project1' />
        <a href={'https://charlielaver.github.io/netflix-in-react-deploy/'} className='btn btn-outline-light project-btn' id='btn1' >Go to Site</a>
        <Arrow link='#project2' />
        </div>

        <div>
        <Iframe url={'https://charlielaver.github.io/youtube-in-react-deploy/'} className="project" id='project2' />
        <a href={'https://charlielaver.github.io/youtube-in-react-deploy/'} className='btn btn-outline-light project-btn' id='btn2' >Go to Site</a>
        <Arrow link='#project3' />
        </div>

        <div>
        <Iframe url={'https://weather-app-13987.web.app/'} className="project" id='project3' />
        <div className='btn-containers'>
        <a href={'https://weather-app-13987.web.app/'} className='btn btn-outline-light project-btn' id='btn3' >Go to Site</a>
        <a href={'https://weather-app-13987.web.app/'} className='btn btn-outline-light project-btn' id='btn3s' >Source Code</a>
        </div>
        <Arrow link='#contact' />
        </div>
         
 
    </div>
    )
}

export default Projects
