import React, { useState } from 'react';
import { FormControl } from 'react-bootstrap';
import './contact.css';
import firebase from 'firebase';
import { db } from './firebase';

const Contact = () => {

    const [name,setName] = useState("");
    const [email,setEmail] = useState("");
    const [subject,setSubject] = useState("");
    const [message,setMessage] = useState("");

    const handleSubmit = (e) => {
      e.preventDefault();

      db.collection('mail').add({
        to: 'deanlaver@yahoo.co.uk',
        message: {
          name: name,
          email: email,
          subject: subject,
          message:message,
        }
      }).then(() => alert('email sent!!'));

      setName('');
      setEmail('');
      setSubject('');
      setMessage('');
    };

    

/*
    const formSubmit = () => {

        admin.firestore().collection('mail').add({
            to: 'charlielaver7@gmail.com',
            message: {
              subject: 'Hello from Firebase!',
              text: 'This is the plaintext section of the email body.',
              html: 'This is the <code>HTML</code> section of the email body.',
            }
          }).then(() => console.log('Queued email for delivery!'));
    }
    */

   return (
    <form id='contact' onSubmit={handleSubmit}>
        <h1>Get in Touch</h1>
        
        <div className='input-box'><FormControl placeholder='Name' value={name} onChange={(e) => setName(e.target.value)} required="required" id='message'/></div>
        <div className='input-box'><FormControl placeholder='Email' value={email} onChange={(e) => setEmail(e.target.value)} required="required" /></div>
        <div className='input-box'><FormControl placeholder='Subject' value={subject} onChange={(e) => setSubject(e.target.value)} required="required" /></div>
        <div className='input-box'><FormControl placeholder='Message' as='textarea' rows='5' value={message} onChange={(e) => setMessage(e.target.value)} required="required" /></div>
        <div className='send-btn'><button type='submit' className='btn btn-outline-success send'>Send Message</button></div>
    </form>
)
}

export default Contact
