/*
    useEffect(() => {
        const element = ref.current;
        gsap.from(
            element.querySelector(".down-arrow"),
            {
            scrollTrigger: {
                trigger: '.down-arrow',
                toggleActions: "restart reverse restart reverse",
                //             onEnter onLeave  onEnterBack  onLeaveBack
            },
            y: 70,
            duration: 3,
            
            ease: 'elastic'
        });
      }, []);
*/
    return (
        <div ref={ref} className='arrow-container'>
            <a href={props.link} ><FaIcons.FaArrowCircleDown className='down-arrow'/></a>
        </div>
    )
}

export default Arrow
