import { useRef, useEffect } from "react";
import Iframe from 'react-iframe';
import './project.css';
import { gsap } from "gsap";
import { ScrollTrigger } from "gsap/ScrollTrigger";
import Arrow from '../Arrow/Arrow';
import Textbox from './TextBox/Textbox';

const Projects = () => {

    gsap.registerPlugin(ScrollTrigger);
    const ref = useRef(null);
  

    useEffect(() => {
        const element = ref.current;
        gsap.from(
            element.querySelector("#project1"),
            {
            scrollTrigger: {
                trigger: '#project1',
                toggleActions: "restart reverse restart reverse",
                //             onEnter onLeave  onEnterBack  onLeaveBack
            },
            x: -600,
            duration: 3,
            opacity: 0,
            ease: 'elastic'
        });
      }, []);

      useEffect(() => {
        const element = ref.current;
        gsap.from(
            element.querySelector("#btn1"),
            {
            scrollTrigger: {
                trigger: '#btn1',
                toggleActions: "restart reverse restart reverse",
                //             onEnter onLeave  onEnterBack  onLeaveBack
            },
            x: -600,
            duration: 6,
            opacity: 0,
            ease: 'elastic'
        });
      }, []);

      useEffect(() => {
        const element = ref.current;
        gsap.from(
            element.querySelector("#btns1"),
            {
            scrollTrigger: {
                trigger: '#btns1',
                toggleActions: "restart reverse restart reverse",
                //             onEnter onLeave  onEnterBack  onLeaveBack
            },
            x: -600,
            duration: 6,
            opacity: 0,
            ease: 'elastic'
        });
      }, []);

      useEffect(() => {
        const element = ref.current;
        gsap.from(
            element.querySelector("#project2"),
            {
            scrollTrigger: {
                trigger: '#project2',
                toggleActions: "restart reverse restart reverse",
                //             onEnter onLeave  onEnterBack  onLeaveBack
            },
            x: 600,
            duration: 3,
            opacity: 0,
            ease: 'elastic'
        });
      }, []);

      useEffect(() => {
        const element = ref.current;
        gsap.from(
            element.querySelector("#btn2"),
            {
            scrollTrigger: {
                trigger: '#btn2',
                toggleActions: "restart reverse restart reverse",
                //             onEnter onLeave  onEnterBack  onLeaveBack
            },
            x: 600,
            duration: 6,
            opacity: 0,
            ease: 'elastic'
        });
      }, []);

      useEffect(() => {
        const element = ref.current;
        gsap.from(
            element.querySelector("#btns2"),
            {
            scrollTrigger: {
                trigger: '#btns2',
                toggleActions: "restart reverse restart reverse",
                //             onEnter onLeave  onEnterBack  onLeaveBack
            },
            x: 600,
            duration: 6,
            opacity: 0,
            ease: 'elastic'
        });
      }, []);

      useEffect(() => {
        const element = ref.current;
        gsap.from(
            element.querySelector("#project3"),
            {
            scrollTrigger: {
                trigger: '#project3',
                toggleActions: "restart reverse restart reverse",
                //             onEnter onLeave  onEnterBack  onLeaveBack
            },
            x: -600,
            duration: 3,
            opacity: 0,
            ease: 'elastic'
        });
      }, []);

      
      useEffect(() => {
        const element = ref.current;
        gsap.from(
            element.querySelector("#btn3"),
            {
            scrollTrigger: {
                trigger: '#btn3',
                toggleActions: "restart reverse restart reverse",
                //             onEnter onLeave  onEnterBack  onLeaveBack
            },
            x: -600,
            duration: 6,
            opacity: 0,
            ease: 'elastic'
        });
      }, []);

      useEffect(() => {
        const element = ref.current;
        gsap.from(
            element.querySelector("#btns3"),
            {
            scrollTrigger: {
                trigger: '#btns3',
                toggleActions: "restart reverse restart reverse",
                //             onEnter onLeave  onEnterBack  onLeaveBack
            },
            x: -600,
            duration: 6,
            opacity: 0,
            ease: 'elastic'
        });
      }, []);

      useEffect(() => {
        const element = ref.current;
        gsap.from(
            element.querySelector("#project4"),
            {
            scrollTrigger: {
                trigger: '#project4',
                toggleActions: "restart reverse restart reverse",
                //             onEnter onLeave  onEnterBack  onLeaveBack
            },
            x: 600,
            duration: 3,
            opacity: 0,
            ease: 'elastic'
        });
      }, []);

      
      useEffect(() => {
        const element = ref.current;
        gsap.from(
            element.querySelector("#btn4"),
            {
            scrollTrigger: {
                trigger: '#btn4',
                toggleActions: "restart reverse restart reverse",
                //             onEnter onLeave  onEnterBack  onLeaveBack
            },
            x: 600,
            duration: 6,
            opacity: 0,
            ease: 'elastic'
        });
      }, []);

      useEffect(() => {
        const element = ref.current;
        gsap.from(
            element.querySelector("#btns4"),
            {
            scrollTrigger: {
                trigger: '#btns4',
                toggleActions: "restart reverse restart reverse",
                //             onEnter onLeave  onEnterBack  onLeaveBack
            },
            x: 600,
            duration: 6,
            opacity: 0,
            ease: 'elastic'
        });
      }, []);

      
      

    return (
        <div ref={ref}>

        <div>
        <Iframe url={'https://charlielaver.github.io/google-auth-react-deploy/'} className="project" id='project1' />
        <div className='btns-top'/>
        <div className='btn-container'>
        <Textbox  title={'Firebase Signup System – '} text={'This is a fully functioning signup system, built in react using firebase authentication. I achieved this using asynchronous functions that are called when the signup state is changed.'} />
        <a href={'https://charlielaver.github.io/google-auth-react-deploy/'} className='btn btn-outline-light project-btn' id='btn1' >Go to Site</a>
        <a href={'https://github.com/CharlieLaver/google-auth-react-deploy/tree/master'} className='btn btn-outline-light project-btn btn-gap' id='btns1' >Source Code</a>
        </div>
        <div className='btns-bottom' />
        <Arrow link='#project2' />
        </div>
        
        

        <div>
        <Iframe url={'https://charlielaver.github.io/youtube-in-react-deploy/'} className="project" id='project2' />
        <div className='btns-top'/>
        <div className='btn-container'>
        <Textbox  title={'YouTube UI Clone in React – '} text={'My goal for this project was to build the application using in depth component-based-architecture. I kept every element on the page as an independent component to improve efficiency and reusability.'} />
        <a href={'https://charlielaver.github.io/youtube-in-react-deploy/'} className='btn btn-outline-light project-btn' id='btn2' >Go to Site</a>
        <a href={'https://github.com/CharlieLaver/youtube-in-react-deploy/tree/source'} className='btn btn-outline-light project-btn btn-gap' id='btns2' >Source Code</a>
        </div>
        <div className='btns-bottom' />
        <Arrow link='#project3' />
        </div>

        <div>
        <Iframe url={'https://charlielaver.github.io/netflix-in-react-deploy/'} className="project" id='project3' />
        <div className='btns-top'/>
        <div className='btn-container'>
        <Textbox title={'Netflix UI Clone in React – '} text={'I built a replica of the popular UI in ReactJS. My main objective for this project was to follow a specification set by an existing interface.'} />
        <a href={'https://charlielaver.github.io/netflix-in-react-deploy/'} className='btn btn-outline-light project-btn' id='btn3' >Go to Site</a>
        <a href={'https://github.com/CharlieLaver/netflix-in-react-deploy/tree/master'} className='btn btn-outline-light project-btn btn-gap' id='btns3' >Source Code</a>
        </div>
        <div className='btns-bottom' />
        <Arrow link='#project4' />
        </div>

        <div>
        <Iframe url={'https://weather-app-13987.web.app/'} className="project" id='project4' />
        <div className='btns-top'/>
        <div className='btn-container'>
        <Textbox title={'Weather API App – '} text={'In this application you can search for any city or country and it will return the weather in the given place. I accomplished this by fetching data from an API, destructing the JSON object and finally displaying the needed data in the UI.'} />
        <a href={'https://weather-app-13987.web.app/'} className='btn btn-outline-light project-btn' id='btn4' >Go to Site</a>
        <a href={'https://github.com/CharlieLaver/react-projects/tree/main/weather-app'} className='btn btn-outline-light project-btn btn-gap' id='btns4' >Source Code</a>
        </div>
        <div className='btns-bottom' />
        <Arrow link='#contact' />
        </div>
         
 
    </div>
    )
}

export default Projects
