import React, { Component } from 'react';
import { FormControl } from 'react-bootstrap';
import './contact.css';
import axios from 'axios';


export default class Form extends Component {

    //form validation
    validateForm = () => {
        let myregex = new RegExp(/^[a-zA-Z0-9 _+-]*$/);
        let emailRegex = new RegExp(/^([a-zA-Z0-9_\-\.]+)@([a-zA-Z0-9_\-\.]+)\.([a-zA-Z]{2,5})$/);
        let message = document.forms["myForm"]["message"].value;
        let email = document.forms["myForm"]["email"].value;
        let name = document.forms["myForm"]["name"].value;
        let subject = document.forms["myForm"]["subject"].value;


        if(!myregex.test(name))
        {
            alert("Name not valid");
            return false;
        }

        if(!emailRegex.test(email))
        {
            alert("Email not valid");
            return false;
        }

        if(!myregex.test(subject))
        {
            alert("Subject not valid");
            return false;
        }

        if(!myregex.test(message))
        {
            alert("Message not valid");
            return false;
        }
        
        this.formSubmit();
    }


    state={
        name:'',
        email:'',
        subject:'',
        message:'',
        sent:false
    }

    // handle inputs

    handleName=(e)=>{
        this.setState({
            name:e.target.value
    })
    }

    handleEmail=(e)=>{
        this.setState({
            email:e.target.value
    })
    }

    handleSubject=(e)=>{
        this.setState({
            subject:e.target.value
    })
    }

    handleMessage=(e)=>{
        this.setState({
            message:e.target.value
    })
    }

    //end of handle inputs
    
    formSubmit=(e)=>{
        e.preventDefault();

        let data = {
            name:this.state.name,
            email:this.state.email,
            subject:this.state.subject,
            message:this.state.message
        }

        axios.post('http://localhost:3001/',data)
        .then(res=>{
            this.setState({
                sent:true,
            },this.resetForm())
        }).catch(()=>{
            console.log('message not sent');
        })
    }

    // for reseting initial data
    resetForm = () => {
      this.setState({
          name:'',
          email:'',
          subject:'',
          message:''
      })

      setTimeout = () => {
          this.setState({
              sent:false,
          },3000)
      };
    }


render() {
    return ( 
        <>

        <form onSubmit={this.formSubmit} id='contact'>
            <h1>Get in Touch</h1>
            <FormControl className='input-box input1' type='text' placeholder='Name' name='name' value={this.state.name} onChange={this.handleName} required/>
            <FormControl className='input-box input2' type='text' placeholder='Email' name='email' value={this.state.email} onChange={this.handleEmail} required/>
            <FormControl className='input-box input3' type='text' placeholder='Subject' name='subject' value={this.state.subject} onChange={this.handleSubject} required/>
            <FormControl className='input-box input4' type='text' placeholder='Message'as='textarea' rows='5' name='message'value={this.state.message} onChange={this.handleMessage} required/>
            <div className={this.state.sent ?'msg msgAppear':'msg'}>Message has been sent</div>
            <div className='send-btn'><button type='submit' className='btn btn-outline-success'>Send Message</button></div>

        </form>
         
        </>
    )
}

}
