import React, { Component, useRef, useEffect } from 'react';
import { FormControl } from 'react-bootstrap';
import './contact.css';
import axios from 'axios';
import { gsap } from "gsap";
import { ScrollTrigger } from "gsap/ScrollTrigger";
import firebase from 'firebase';


export default class Form extends Component {

    formSubmit = () => {

        admin.firestore().collection('mail').add({
            to: 'someone@example.com',
            message: {
              subject: 'Hello from Firebase!',
              text: 'This is the plaintext section of the email body.',
              html: 'This is the <code>HTML</code> section of the email body.',
            }
          }).then(() => console.log('Queued email for delivery!'));
    }
    

render() {
    return ( 
        <>

        <form  id='contact'>
            <h1>Get in Touch</h1>
            <FormControl className='input-box input1' type='text' placeholder='Name' id='name'  required/>
            <FormControl className='input-box input2' type='text' placeholder='Email' id='email'  required/>
            <FormControl className='input-box input3' type='text' placeholder='Subject' id='subject'  required/>
            <FormControl className='input-box input4' type='text' placeholder='Message'as='textarea' rows='5' id='message' required/>
            
            <div className='send-btn'><button type='submit' className='btn btn-outline-success send'>Send Message</button></div>

        </form>
         
        </>
    )
}

}
