import { useRef, useEffect } from "react";
import Iframe from 'react-iframe';
import './project.css';
import { gsap } from "gsap";
import { ScrollTrigger } from "gsap/ScrollTrigger";

const Projects = () => {

    gsap.registerPlugin(ScrollTrigger);
    const ref = useRef(null);
  

    useEffect(() => {
        const element = ref.current;
        gsap.from(
            element.querySelector("#project1"),
            {
            scrollTrigger: {
                trigger: '#project1',
                toggleActions: "restart reverse restart reverse",
                //             onEnter onLeave  onEnterBack  onLeaveBack
            },
            x: -600,
            duration: 3,
            opacity: 0,
            ease: 'none'
        });
      }, []);

      useEffect(() => {
        const element = ref.current;
        gsap.from(
            element.querySelector("#project2"),
            {
            scrollTrigger: {
                trigger: '#project2',
                toggleActions: "restart reverse restart reverse",
                //             onEnter onLeave  onEnterBack  onLeaveBack
            },
            x: 600,
            duration: 3,
            opacity: 0,
            ease: 'none'
        });
      }, []);

      useEffect(() => {
        const element = ref.current;
        gsap.from(
            element.querySelector("#project3"),
            {
            scrollTrigger: {
                trigger: '#project3',
                toggleActions: "restart reverse restart reverse",
                //             onEnter onLeave  onEnterBack  onLeaveBack
            },
            x: -600,
            duration: 3,
            opacity: 0,
            ease: 'none'
        });
      }, []);

    return (
        <div ref={ref}>
        
        <div>
        <Iframe url={'https://charlielaver.github.io/netflix-in-react-deploy/'} className="project" id='project1' />
        <div className='arrow arrow-first'>

        </div>

        <div className='arrow arrow-second'>
            
        </div>
        </div>

        <div>
        <Iframe url={'https://charlielaver.github.io/rba-react-deploy/'} className="project" id='project2' />
        </div>

        <div>
        <Iframe url={'http://www.lucindalaverot.co.uk/'} className="project" id='project3' />
        </div>
         
 
    </div>
    )
}

export default Projects
