import React from 'react';
import Particles from 'react-particles-js';

const background = () => {
    return (
        <Particles
        params={{
          "particles": {
            "number": {
              "value": 150
            },
            "size": {
              "value": 3
            }
          },
          "interactivity": {
            "events": {
              "onhover": {
                "enable": true,
                "mode": "repulse"
              }
            }
          }
        }} 
        style={{
          //'position': 'fixed',
          'z-index': -1
        }}
        />
    )
}

export default background
