import React from 'react';
import { FormControl } from 'react-bootstrap';
import './contact.css';
import { gsap } from "gsap";
import { ScrollTrigger } from "gsap/ScrollTrigger";
import { useRef, useEffect } from "react";


const Contact = () => {

    const state={
        name:'',
        email:'',
        subject:'',
        message:'',
        sent:false
    }

    // handle inputs
    const handleName=(e)=>{
        this.setState({
            name:e.target.name
    })
    }

    const handleEmail=(e)=>{
        this.setState({
            email:e.target.email
    })
    }

    const handleSubject=(e)=>{
        this.setState({
            subject:e.target.subject
    })
    }

    const handleMessage=(e)=>{
        this.setState({
            message:e.target.message
    })
    }



    //end of handle inputs



    gsap.registerPlugin(ScrollTrigger);
    const ref = useRef(null);

    
  useEffect(() => {
    const element = ref.current;
    gsap.from(
        element.querySelector(".input1"),
        {
        scrollTrigger: {
            trigger: '.input1',
            toggleActions: "restart reverse restart reverse",
            //             onEnter onLeave  onEnterBack  onLeaveBack
        },
        x: 300,
        duration: 3,
        opacity: 0,
        ease: 'none'
    });
  }, []);

  
  useEffect(() => {
    const element = ref.current;
    gsap.from(
        element.querySelector(".input2"),
        {
        scrollTrigger: {
            trigger: '.input2',
            toggleActions: "restart reverse restart reverse",
            //             onEnter onLeave  onEnterBack  onLeaveBack
        },
        x: -300,
        duration: 3,
        opacity: 0,
        ease: 'none'
    });
  }, []);

  
  useEffect(() => {
    const element = ref.current;
    gsap.from(
        element.querySelector(".input3"),
        {
        scrollTrigger: {
            trigger: '.input3',
            toggleActions: "restart reverse restart reverse",
            //             onEnter onLeave  onEnterBack  onLeaveBack
        },
        x: 300,
        duration: 3,
        opacity: 0,
        ease: 'none'
    });
  }, []);

  
  useEffect(() => {
    const element = ref.current;
    gsap.from(
        element.querySelector(".input4"),
        {
        scrollTrigger: {
            trigger: '.input4',
            toggleActions: "restart reverse restart reverse",
            //             onEnter onLeave  onEnterBack  onLeaveBack
        },
        x: -300,
        duration: 3,
        opacity: 0,
        ease: 'none'
    });
  }, []);

    return (
        <>

        <form id='contact' ref={ref} >
            <h1>Get in Touch</h1>
            <FormControl className='input-box input1' placeholder='Name'/>
            <FormControl className='input-box input2' placeholder='Email'/>
            <FormControl className='input-box input3' placeholder='Subject'/>
            <FormControl className='input-box input4' placeholder='Message'as='textarea' rows='5'/>
            <div className='msg'>Message has been sent</div>
            <div className='send-btn'><button type='submit' className='btn btn-outline-success'>Send Message</button></div>

        </form>
         
        </>
    )
}

export default Contact
