import { useRef, useEffect } from "react";
import Iframe from 'react-iframe';
import './project.css';
import { gsap } from "gsap";
import { ScrollTrigger } from "gsap/ScrollTrigger";

const Projects = () => {

    gsap.registerPlugin(ScrollTrigger);
    const ref = useRef(null);
  

    useEffect(() => {
        const element = ref.current;
        gsap.from(
            element.querySelector("#project1"),
            {
            scrollTrigger: {
                trigger: '#project1',
                toggleActions: "restart reverse restart reverse",
                //             onEnter onLeave  onEnterBack  onLeaveBack
            },
            x:-400,
            duration: 3,
            opacity: 0,
            ease: 'none'
        });
      }, []);

    return (
        <div ref={ref}>
        
        
        <Iframe url={'https://charlielaver.github.io/netflix-in-react-deploy/'}
        className="project"
        id='project1'
        />
         <a href='#project2'><div class="arrowDown hidden-xs">
                          <span></span>
                          <span></span>
                          <span></span>
                        </div></a>

        <Iframe url={'https://charlielaver.github.io/netflix-in-react-deploy/'}
        className="project"
        id='project2'
        />

        <Iframe url={'https://charlielaver.github.io/netflix-in-react-deploy/'}
        className="project"
        id='project1'
        />
        
      
    </div>
    )
}

export default Projects
