import { useRef, useEffect } from "react";
import './footer.css';
import w3cert from './Images/w3cert.png';
import { gsap } from "gsap";
import { ScrollTrigger } from "gsap/ScrollTrigger";

const Footer = () => {

    gsap.registerPlugin(ScrollTrigger);
    const ref = useRef(null);
  

    useEffect(() => {
        const element = ref.current;
        gsap.from(
            element.querySelector(".cert1"),
            {
            scrollTrigger: {
                trigger: '.cert1',
                toggleActions: "restart reverse restart reverse",
                //             onEnter onLeave  onEnterBack  onLeaveBack
            },
            x: 100,
            duration: 6,
            opacity: 0,
            ease: 'elastic'
        });
      }, []);

    return (
            <div ref={ref} className='footer-item'>
                <a href='https://certification.w3schools.com/w3certified.asp?email=charlielaver7@gmail.com'><img src={w3cert} alt='cert' className='cert1'/></a>
            </div>
    )
}

 export default Footer
