import { useRef, useEffect } from "react";
import Iframe from 'react-iframe';
import './project.css';
import { gsap } from "gsap";
import { ScrollTrigger } from "gsap/ScrollTrigger";
import Arrow from '../Arrow/Arrow';

const Projects = () => {

    gsap.registerPlugin(ScrollTrigger);
    const ref = useRef(null);
  

    useEffect(() => {
        const element = ref.current;
        gsap.from(
            element.querySelector("#project1"),
            {
            scrollTrigger: {
                trigger: '#project1',
                toggleActions: "restart reverse restart reverse",
                //             onEnter onLeave  onEnterBack  onLeaveBack
            },
            x: -600,
            duration: 3,
            opacity: 0,
            ease: 'elastic'
        });
      }, []);

      useEffect(() => {
        const element = ref.current;
        gsap.from(
            element.querySelector("#btn1"),
            {
            scrollTrigger: {
                trigger: '#btn1',
                toggleActions: "restart reverse restart reverse",
                //             onEnter onLeave  onEnterBack  onLeaveBack
            },
            x: -600,
            duration: 6,
            opacity: 0,
            ease: 'elastic'
        });
      }, []);

      useEffect(() => {
        const element = ref.current;
        gsap.from(
            element.querySelector("#btns1"),
            {
            scrollTrigger: {
                trigger: '#btns1',
                toggleActions: "restart reverse restart reverse",
                //             onEnter onLeave  onEnterBack  onLeaveBack
            },
            x: -600,
            duration: 6,
            opacity: 0,
            ease: 'elastic'
        });
      }, []);

      useEffect(() => {
        const element = ref.current;
        gsap.from(
            element.querySelector("#project2"),
            {
            scrollTrigger: {
                trigger: '#project2',
                toggleActions: "restart reverse restart reverse",
                //             onEnter onLeave  onEnterBack  onLeaveBack
            },
            x: 600,
            duration: 3,
            opacity: 0,
            ease: 'elastic'
        });
      }, []);

      useEffect(() => {
        const element = ref.current;
        gsap.from(
            element.querySelector("#btn2"),
            {
            scrollTrigger: {
                trigger: '#btn2',
                toggleActions: "restart reverse restart reverse",
                //             onEnter onLeave  onEnterBack  onLeaveBack
            },
            x: 600,
            duration: 6,
            opacity: 0,
            ease: 'elastic'
        });
      }, []);

      useEffect(() => {
        const element = ref.current;
        gsap.from(
            element.querySelector("#btns2"),
            {
            scrollTrigger: {
                trigger: '#btns2',
                toggleActions: "restart reverse restart reverse",
                //             onEnter onLeave  onEnterBack  onLeaveBack
            },
            x: 600,
            duration: 6,
            opacity: 0,
            ease: 'elastic'
        });
      }, []);

      useEffect(() => {
        const element = ref.current;
        gsap.from(
            element.querySelector("#project3"),
            {
            scrollTrigger: {
                trigger: '#project3',
                toggleActions: "restart reverse restart reverse",
                //             onEnter onLeave  onEnterBack  onLeaveBack
            },
            x: -600,
            duration: 3,
            opacity: 0,
            ease: 'elastic'
        });
      }, []);

      
      useEffect(() => {
        const element = ref.current;
        gsap.from(
            element.querySelector("#btn3"),
            {
            scrollTrigger: {
                trigger: '#btn3',
                toggleActions: "restart reverse restart reverse",
                //             onEnter onLeave  onEnterBack  onLeaveBack
            },
            x: -600,
            duration: 6,
            opacity: 0,
            ease: 'elastic'
        });
      }, []);

      useEffect(() => {
        const element = ref.current;
        gsap.from(
            element.querySelector("#btns3"),
            {
            scrollTrigger: {
                trigger: '#btns3',
                toggleActions: "restart reverse restart reverse",
                //             onEnter onLeave  onEnterBack  onLeaveBack
            },
            x: -600,
            duration: 6,
            opacity: 0,
            ease: 'elastic'
        });
      }, []);

      
      

    return (
        <div ref={ref}>
        
        <div>
        <Iframe url={'https://charlielaver.github.io/netflix-in-react-deploy/'} className="project" id='project1' />
        <div className='btns-top'/>
        <div className='btn-container'>
        <a href={'https://charlielaver.github.io/netflix-in-react-deploy/'} className='btn btn-outline-light project-btn' id='btn1' >Go to Site</a>
        <a href={'https://github.com/CharlieLaver/netflix-in-react-deploy'} className='btn btn-outline-light project-btn' id='btns1' >Source Code</a>
        </div>
        <div className='btns-bottom' />
        <Arrow link='#project2' />
        </div>

        <div>
        <Iframe url={'https://charlielaver.github.io/youtube-in-react-deploy/'} className="project" id='project2' />
        <div className='btns-top'/>
        <div className='btn-container'>
        <a href={'https://charlielaver.github.io/youtube-in-react-deploy/'} className='btn btn-outline-light project-btn' id='btn2' >Go to Site</a>
        <a href={'https://github.com/CharlieLaver/youtube-in-react-deploy'} className='btn btn-outline-light project-btn btn-gap' id='btns2' >Source Code</a>
        </div>
        <div className='btns-bottom' />
        <Arrow link='#project3' />
        </div>

        <div>
        <Iframe url={'https://weather-app-13987.web.app/'} className="project" id='project3' />
        <div className='btns-top'/>
        <div className='btn-container'>
        <a href={'https://weather-app-13987.web.app/'} className='btn btn-outline-light project-btn' id='btn3' >Go to Site</a>
        <a href={'https://github.com/CharlieLaver'} className='btn btn-outline-light project-btn' id='btns3' >Source Code</a>
        </div>
        <div className='btns-bottom' />
        <Arrow link='#contact' />
        </div>
         
 
    </div>
    )
}

export default Projects
