import React from 'react'
import './home.css';
import { gsap } from "gsap";
import { ScrollTrigger } from "gsap/ScrollTrigger";
import { useRef, useEffect } from "react";


const Home = () => {

  gsap.registerPlugin(ScrollTrigger);
  const ref = useRef(null);

  useEffect(() => {
    const element = ref.current;
    gsap.from(
        element.querySelector(".home-text"),
        {
        scrollTrigger: {
            trigger: '.home-text',
            toggleActions: "restart reverse restart reverse",
            //             onEnter onLeave  onEnterBack  onLeaveBack
        },
        y: 80,
        duration: 3,
        opacity: 0,
        ease: 'elastic'
    });
  }, []);


    return (
        <>
          <div id='home' ref={ref}>
            <div className='home-text'>
            <p className='title'>Hello, my name is Charlie,</p>
            <p>I am a JavaScript developer with a passion for UI design. I have experience working with React, Node & Vanilla JS. Below you can view some examples of my work.</p>
            </div>
        
           </div> 
        </>
    )
}

export default Home
