import React from 'react'
import './home.css';
import * as FaIcons from 'react-icons/fa';
import { gsap } from "gsap";
import { ScrollTrigger } from "gsap/ScrollTrigger";
import { useRef, useEffect } from "react";


const Home = () => {

  gsap.registerPlugin(ScrollTrigger);
  const ref = useRef(null);

  useEffect(() => {
    const element = ref.current;
    gsap.from(
        element.querySelector(".home-text"),
        {
        scrollTrigger: {
            trigger: '.home-text',
            toggleActions: "restart reverse restart reverse",
            //             onEnter onLeave  onEnterBack  onLeaveBack
        },
        y: 80,
        duration: 3,
        opacity: 0,
        ease: 'elastic'
    });
  }, []);

  useEffect(() => {
    const element = ref.current;
    gsap.from(
        element.querySelector(".home-arrow"),
        {
        scrollTrigger: {
            trigger: '.home-arrow',
            toggleActions: "restart reverse restart reverse",
            //             onEnter onLeave  onEnterBack  onLeaveBack
        },
        y: 150,
        duration: 5,
        opacity: 0,
        ease: 'elastic'
    });
  }, []);

    return (
        <>
          <div id='home' ref={ref}>
            <div className='home-text'>
            <p className='title'>Hello, my name is Charlie,</p>
            <p>I am a front-end developer from the UK. I program in JavaScript as well as working with many front-end technologies. I specialise in building component-based interfaces with ReactJS.</p>
            </div>
            
           </div> 
        </>
    )
}

export default Home
