import React, { useState } from 'react';
import { FormControl } from 'react-bootstrap';
import './contact.css';
import firebase from 'firebase';
import { db } from './firebase';
import emailjs from 'emailjs-com';

const Contact = () => {

    const [name,setName] = useState("");
    const [email,setEmail] = useState("");
    const [subject,setSubject] = useState("");
    const [message,setMessage] = useState("");

    const handleSubmit = (e) => {
      e.preventDefault();

      emailjs.sendForm('service_92p2bns', 'template_t2t9sb3', e.target, 'user_hcMT3qjFKeDeR3KT4Zt04')
        .then((result) => {
            console.log(result.text);
        }, (error) => {
            console.log(error.text);
        });
        e.target.reset()

    };
/*
firebase mail trigger
    const handleSubmit = (e) => {
      e.preventDefault();

      db.collection('mail').add({
        to: 'lankydragonqueries@gmail.com',
        message: {
          name: name,
          email: email,
          subject: subject,
          message:message,
        }
      }).then(() => alert('email sent!!'));

      setName('');
      setEmail('');
      setSubject('');
      setMessage('');
    };

    */

   return (
    <form id='contact' onSubmit={handleSubmit}>
        <h1>Get in Touch</h1>
        
        <div className='input-box'><FormControl placeholder='Name' name='name' value={name} onChange={(e) => setName(e.target.value)} required="required" id='message'/></div>
        <div className='input-box'><FormControl placeholder='Email' name='email' value={email} onChange={(e) => setEmail(e.target.value)} required="required" /></div>
        <div className='input-box'><FormControl placeholder='Subject' name='subject' value={subject} onChange={(e) => setSubject(e.target.value)} required="required" /></div>
        <div className='input-box'><FormControl placeholder='Message' as='textarea' rows='5' name='message' value={message} onChange={(e) => setMessage(e.target.value)} required="required" /></div>
        <div className='send-btn'><button type='submit' className='btn btn-outline-success send'>Send Message</button></div>
    </form>
)
}

export default Contact
